import React, { useState } from "react";
import styled from "styled-components";


const Tab = styled.button`
  padding: 10px 30px;
  cursor: pointer;
  opacity: 0.6;
  background: white;
  border: 0;
  outline: 0;
  font-size: 18px;
  font-weight: 500;
  border-bottom: 2px solid transparent;
  transition: ease border-bottom 250ms;
  ${({ active }) =>
    active &&
    `
    border-bottom: 2px solid black;
    font-weight: bold;
    opacity: 1;
  `}
`;


function TabGroupTestComp(props) {
  const [active, setActive] = useState(this.props.types[0]);
  return (
      <div>
          {props.types.map(type => {
              return (
                  <Tab
                    key={type}
                    active={active === type}
                    onClick={() => {setActive(type);}}>
                    {type.title}
                  </Tab>
              )
          })}
      </div>
  );
}



export default function App() {
  return (
      <TabGroupTestComp types={this.props.types}/>
  );
}
